workspace.query:
# # show all the matches in the triplestore # #
PREFIX dbont: <http://dbpedia.org/ontology/> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
select ?matchIRI ?matchName where { ?matchIRI a dbont:FootballMatch ; foaf:name ?matchName . }

# # show all the players in the triplestore and their names # Note: some players have more than one name #
PREFIX dbont: <http://dbpedia.org/ontology/> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
select distinct ?s ?playerName where { ?s a dbont:SoccerPlayer ; foaf:name ?playerName . }

# # what can SPARQL tell me about a player? # show me all triples where the first soccer player is the subject #
PREFIX dbont: <http://dbpedia.org/ontology/>
select ?s ?p ?o where { ?s ?p ?o . { select ?s where { ?s a dbont:SoccerPlayer } limit 1 } }

# # what can SPARQL tell me about a team? # show me all triples where the first soccer player is the subject #
PREFIX dbont: <http://dbpedia.org/ontology/>
select ?s ?p ?o where { ?s ?p ?o . { select ?s where { ?s a dbont:SoccerClub } limit 1 } }

# # what can SPARQL tell me about a match? # show me all triples where the first match is the subject #
PREFIX dbont: <http://dbpedia.org/ontology/>
select ?s ?p ?o where { ?s ?p ?o . { select ?s where { ?s a dbont:FootballMatch } limit 1 } }

(: # # Call out to the CIA World FactBook SPARQL endpoint # query for the first 10 triples # Note: to insert the results into the database, use CONSTRUCT (CONSTRUCT returns triples, not solutions) # # Requires a network connection # # Hint: some interesting factbook predicates: # - <http://wifo5-04.informatik.uni-mannheim.de/factbook/ns#climate> - climate of a country # - <http://www.daml.org/2001/12/factbook/factbook-ont#internationalDispute> - international disputes a country is involved in # :)
let $sparql := ' select ?s ?p ?o where { ?s ?p ?o } limit 10 '
let $sparql-url-encoded := xdmp:url-encode( $sparql )
let $endpoint := 'http://wifo5-04.informatik.uni-mannheim.de/factbook/sparql'
let $url := fn:concat( $endpoint, "?query=", $sparql-url-encoded )
return xdmp:http-get( $url )

/*
#
#  Call out to the CIA World FactBook SPARQL endpoint
#  query for the first 10 triples
#  Note: to insert the results into the database, use CONSTRUCT (CONSTRUCT returns triples, not solutions)
#
#  Requires a network connection
#
#  Hint: some interesting factbook predicates:
#    - <http://wifo5-04.informatik.uni-mannheim.de/factbook/ns#climate> - climate of a country
#    - <http://www.daml.org/2001/12/factbook/factbook-ont#internationalDispute> - international disputes a country is involved in
#
*/

var sparql = ' select ?s ?p ?o where { ?s ?p ?o } limit 10 ' ;

var sparqlUrlEncoded =
  xdmp.urlEncode( sparql ) ;

var endpoint =
  'http://wifo5-04.informatik.uni-mannheim.de/factbook/sparql' ;

var url =
  fn.concat(
    endpoint,
    "?query=",
    sparqlUrlEncoded
    ) ;


  xdmp.httpGet( url ) ;

(: # # Call out to the dbpedia SPARQL endpoint # query for the first 10 triples about te Mexico national football team # Note: to insert the results into the database, use CONSTRUCT (CONSTRUCT returns triples, not solutions) # :)
let $sparql := ' PREFIX : <http://dbpedia.org/resource/> select ?p ?o where { :Mexico_national_football_team ?p ?o } limit 10 '
let $sparql-url-encoded := xdmp:url-encode( $sparql )
let $endpoint := 'http://dbpedia.org/sparql'
let $url := fn:concat( $endpoint, "?query=", $sparql-url-encoded )
return xdmp:http-get( $url )

/*
#
#  Call out to the dbpedia SPARQL endpoint
#  query for the first 10 triples about the Mexico national football team
#  Note: to insert the results into the database, use CONSTRUCT (CONSTRUCT returns triples, not solutions)
#
*/

var sparql = '\
PREFIX : <http://dbpedia.org/resource/>                                \
select ?p ?o where { :Mexico_national_football_team ?p ?o } limit 10   \
' ;

var sparqlUrlEncoded =
  xdmp.urlEncode( sparql ) ;

var endpoint =
  'http://dbpedia.org/sparql' ;

var url =
  fn.concat(
    endpoint,
    "?query=",
    sparqlUrlEncoded
    ) ;


  xdmp.httpGet( url ) ;

